import React, { useState, useEffect } from 'react';
import './Showcase.css'; // Ensure you have this CSS file

const VideoShowcase = () => {
    const [currentVideo, setCurrentVideo] = useState(null); // State to hold the current video source
    const [isTextVisible, setIsTextVisible] = useState(false); // State to control text visibility
    const [view, setView] = useState('showcase'); // State to control the current view

    // Define video sources
    const videos = {
        hello: '/videos/hello.mp4', // Replace with your video paths
        thanks: '/videos/thanks.mp4',
        iloveyou: '/videos/love.mp4',
    };

    // Function to handle video button click
    const handleVideoClick = (videoKey) => {
        setCurrentVideo(videos[videoKey]);
    };

    // Fade in effect on component mount
    useEffect(() => {
        const timer = setTimeout(() => {
            setIsTextVisible(true); // Set text to visible after 1 second
        }, 1000); // Adjust the delay as needed

        return () => clearTimeout(timer); // Clean up the timer
    }, []);

    // Function to toggle view
    const toggleView = () => {
        setView((prevView) => (prevView === 'showcase' ? 'details' : 'showcase'));
    };

    return (
        <div className="video-showcase">
            <div className="menu-icon" onClick={toggleView}>
                {/* Hamburger menu icon */}
                <div className="line"></div>
                <div className="line"></div>
                <div className="line"></div>
            </div>

            {view === 'showcase' && (
                <>
                    <div className="video-buttons">
                        <button onClick={() => handleVideoClick('hello')}>Hello</button>
                        <button onClick={() => handleVideoClick('thanks')}>Thanks</button>
                        <button onClick={() => handleVideoClick('iloveyou')}>I Love You</button>
                    </div>

                    {currentVideo && (
                        <div className="video-container">
                            <video 
                                src={currentVideo} 
                                controls 
                                autoPlay 
                                style={{ width: '100%', maxHeight: '400px' }} 
                            />
                        </div>
                    )}

                    <div className="description">
                        <div className={`description-text ${isTextVisible ? 'fade-in' : ''}`}>
                            Hey! This is a demo of an ASL Interpreter. Check out more information in the top left!
                        </div>
                    </div>
                </>
            )}

            {view === 'details' && (
                <div className="project-details">
                    {/* Details about the project */}
                    <h2>Project Details</h2>
                    <p>This project includes several features that demonstrate advanced video handling and user interactivity.</p>
                    <p>You can navigate between video showcases and detailed information seamlessly.</p>
                </div>
            )}
        </div>
    );
};

export default VideoShowcase;
