import React, { useEffect, useRef, useState } from 'react';
import './CameraComponent.css';

const CameraComponent = () => {
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    const [isSending, setIsSending] = useState(false); // State to control frame sending
    const [intervalId, setIntervalId] = useState(null); // Use state to store the interval ID

    // Function to get access to the webcam
    const getVideo = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({
                video: true,
            });
            videoRef.current.srcObject = stream;
        } catch (error) {
            console.error("Error accessing webcam:", error);
        }
    };

    // Function to send the captured frame to the backend
    const sendFrame = async () => {
        const canvas = canvasRef.current;
        const video = videoRef.current;

        // Set canvas dimensions to match the video feed
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;

        // Draw the video frame to the canvas
        const context = canvas.getContext('2d');
        context.drawImage(video, 0, 0, canvas.width, canvas.height);
        
        // Convert the canvas content to a Base64 string
        const data = canvas.toDataURL('image/jpeg');

        console.log("Sending frame..."); // Log to check if this runs
        try {
            const response = await fetch('http://localhost:5000/video_feed', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ image: data }), // Send the image data
            });
    
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
    
            const result = await response.json();
            console.log("Frame processed:", result); // Handle the backend response
        } catch (error) {
            console.error('Error sending frame:', error);
        }
    };

    // Function to start sending frames
    const handleStart = () => {
        if (!isSending) {
            setIsSending(true); // Update state to indicate sending has started
            const id = setInterval(sendFrame, 1000 / 30); // Send frame every 33.33 ms for ~30 fps
            setIntervalId(id); // Store the interval ID
        }
    };

    // Clean up interval on component unmount or when sending stops
    useEffect(() => {
        getVideo(); // Start video feed

        return () => {
            if (intervalId) {
                clearInterval(intervalId); // Clear the interval on unmount or stop
            }
        };
    }, [intervalId]); // Only run once on mount or when intervalId changes

    return (
        <div className="camera-container">
            <video ref={videoRef} autoPlay className="camera-feed" />
            <canvas ref={canvasRef} style={{ display: 'none' }} /> {/* Hidden canvas for drawing */}
            <button 
                onClick={handleStart} 
                disabled={isSending} 
                style={{
                    backgroundColor: isSending ? 'darkgray' : 'blue', // Change color when sending
                    color: 'white',
                    cursor: isSending ? 'not-allowed' : 'pointer',
                }}
            >
                {isSending ? 'Sending...' : 'Start Sending Frames'}
            </button> {/* Button to start sending frames */}
        </div>
    );
};

export default CameraComponent;
