import React, { useEffect, useRef } from 'react';
import './CameraComponent.css';

const CameraComponent = () => {
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    let intervalId = useRef(null); // Use ref to store interval ID

    // Function to get access to the webcam
    const getVideo = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({
                video: true,
            });
            videoRef.current.srcObject = stream;
        } catch (error) {
            console.error("Error accessing webcam:", error);
        }
    };

    // Function to send the captured frame to the backend
    const sendFrame = async () => {
        const canvas = canvasRef.current;
        const video = videoRef.current;

        // Set canvas dimensions to match the video feed
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;

        // Draw the video frame to the canvas
        const context = canvas.getContext('2d');
        context.drawImage(video, 0, 0, canvas.width, canvas.height);
        
        // Convert the canvas content to a Base64 string
        const data = canvas.toDataURL('image/jpeg');

        console.log("Sending frame..."); // Log to check if this runs
        try {
            const response = await fetch('http://localhost:5000/video_feed', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ image: data }), // Send the image data
            });
    
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
    
            const result = await response.json();
            console.log("Frame processed:", result); // Handle the backend response
        } catch (error) {
            console.error('Error sending frame:', error);
        }
    };

    // UseEffect for setting up video feed and sending frames
    useEffect(() => {
        getVideo(); // Start video feed

        // Set an interval to send frames continuously
        intervalId.current = setInterval(sendFrame, 1000); // Send frame every second

        // Clean up the interval on component unmount
        return () => {
            clearInterval(intervalId.current);
        };
    }, []); // Only run once on mount

    return (
        <div className="camera-container">
            <video ref={videoRef} autoPlay className="camera-feed" />
            <canvas ref={canvasRef} style={{ display: 'none' }} /> {/* Hidden canvas for drawing */}
        </div>
    );
};

export default CameraComponent;
