import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './PasswordChecker.css';

const PasswordChecker = () => {
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        if (password === 'asl') {
            navigate('/showcase');
        } else {
            setError('Incorrect password! Try again.');
        }
    };

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    return (
        <div className="password-checker-container">
            <h1 className="title">Enter the Password</h1>
            <form onSubmit={handleSubmit} className="password-form">
                <div className="input-group">
                    <input
                        type={showPassword ? 'text' : 'password'}
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Your Secret Password"
                        className="password-input"
                    />
                    <button type="button" onClick={togglePasswordVisibility} className="toggle-visibility">
                        {showPassword ? 'Hide' : 'Show'}
                    </button>
                </div>
                <button type="submit" className="submit-button">Check</button>
                {error && <div className="error-message">{error}</div>}
            </form>
        </div>
    );
};

export default PasswordChecker;
