import React, { useState, useEffect } from 'react';
import './Showcase.css'; // Ensure you have this CSS file

const VideoShowcase = () => {
    const [currentVideo, setCurrentVideo] = useState(null); // State to hold the current video source
    const [isTextVisible, setIsTextVisible] = useState(false); // State to control text visibility
    const [isDescriptionVisible, setIsDescriptionVisible] = useState(false); // State for description visibility
    const [fadeClass, setFadeClass] = useState(''); // State to control fade classes

    // Define video sources
    const videos = {
        hello: '/videos/hello.mp4', // Replace with your video paths
        thanks: '/videos/thanks.mp4',
        iloveyou: '/videos/love.mp4',
    };

    // Function to handle video button click
    const handleVideoClick = (videoKey) => {
        setCurrentVideo(videos[videoKey]);
    };

    // Fade in effect on component mount
    useEffect(() => {
        const timer = setTimeout(() => {
            setIsTextVisible(true); // Set text to visible after 1 second
        }, 1000); // Adjust the delay as needed

        return () => clearTimeout(timer); // Clean up the timer
    }, []);

    // Function to toggle description visibility
    const toggleDescription = () => {
        // Start the fade-out effect
        setFadeClass('fade-out');

        // After the fade-out transition, toggle visibility
        setTimeout(() => {
            setIsDescriptionVisible(!isDescriptionVisible);
            setFadeClass(''); // Reset the fade class after toggling
        }, 500); // Match this duration with your CSS transition duration
    };

    return (
        <div className="video-showcase">
            {/* Hamburger Menu Icon */}
            <div className="menu-icon" onClick={toggleDescription}>
                <div className="line"></div>
                <div className="line"></div>
                <div className="line"></div>
            </div>

            {/* Conditional rendering based on description visibility */}
            {isDescriptionVisible ? (
                <div className={`description ${fadeClass}`}>
                    <div className={`description-text ${isTextVisible ? 'fade-in' : ''}`}>
                        This project showcases a collection of videos that convey different messages. 
                        Hover over the video to see the description fade in.
                    </div>
                </div>
            ) : (
                <>
                    <div className={`video-buttons ${fadeClass}`}>
                        <button onClick={() => handleVideoClick('hello')}>Hello</button>
                        <button onClick={() => handleVideoClick('thanks')}>Thanks</button>
                        <button onClick={() => handleVideoClick('iloveyou')}>I Love You</button>
                    </div>

                    {currentVideo && (
                        <div className={`video-container ${fadeClass}`}>
                            <video 
                                src={currentVideo} 
                                controls 
                                autoPlay 
                                style={{ width: '100%', maxHeight: '400px' }} 
                            />
                        </div>
                    )}
                </>
            )}
        </div>
    );
};

export default VideoShowcase;
