import React, { useEffect, useRef } from 'react';
import './CameraComponent.css';

const CameraComponent = () => {
    const videoRef = useRef(null);
    const canvasRef = useRef(null);

    useEffect(() => {
        const getVideo = async () => {
            const stream = await navigator.mediaDevices.getUserMedia({
                video: true,
            });
            videoRef.current.srcObject = stream;
        };
        getVideo();
    }, []);

    const captureFrame = () => {
        const canvas = canvasRef.current;
        const video = videoRef.current;
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        const context = canvas.getContext('2d');
        context.drawImage(video, 0, 0, canvas.width, canvas.height);
        sendFrame(canvas.toDataURL('image/jpeg')); // Convert to Base64 and send
    };

    const sendFrame = async (data) => {
        try {
            const response = await fetch('http://localhost:5000/process_frame', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ image: data }), // Send the image data
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const result = await response.json();
            console.log(result); // Handle the response
        } catch (error) {
            console.error('Error sending frame:', error);
        }
    };

    return (
        <div className="camera-container">
            <video ref={videoRef} autoPlay />
            <canvas ref={canvasRef} style={{ display: 'none' }} />
            <button onClick={captureFrame}>Capture Frame</button>
        </div>
    );
};

export default CameraComponent;
