import React, { useState } from 'react';
import './PasswordChecker.css'; // Ensure you create this CSS file

const PasswordChecker = () => {
    const [password, setPassword] = useState('');
    const [isCorrect, setIsCorrect] = useState(null); // null = no result, true = correct, false = incorrect

    const handleSubmit = (e) => {
        e.preventDefault(); // Prevent the default form submission
        setIsCorrect(password === 'asl'); // Check if the password is 'asl'
    };

    return (
        <div className="password-checker">
            <h1>Password Checker</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Enter your password"
                    className="password-input"
                />
                <button type="submit" className="submit-button">Check Password</button>
            </form>
            {isCorrect !== null && (
                <div className={`result ${isCorrect ? 'correct' : 'incorrect'}`}>
                    {isCorrect ? 'Password is correct!' : 'Password is incorrect! Try again.'}
                </div>
            )}
        </div>
    );
};

export default PasswordChecker;
