// PredictionComponent.js
import React, { useEffect, useState } from 'react';
import './PredictionComponent.css'; // Import the CSS file

const PredictionComponent = () => {
    const [prediction, setPrediction] = useState("");

    useEffect(() => {
        const interval = setInterval(async () => {
            try {
                const response = await fetch("http://127.0.0.1:5000/prediction");
                const data = await response.json();
                setPrediction(data.prediction);
            } catch (error) {
                console.error("Error fetching prediction:", error);
            }
        }, 1000);

        return () => clearInterval(interval);
    }, []);

    return (
        <div className="prediction-container">
            <h1 className="prediction-heading">Prediction: {prediction}</h1>
            <div className="prediction-box">
                <p className="prediction-text">{prediction || "Waiting for prediction..."}</p>
            </div>
        </div>
    );
};

export default PredictionComponent;
