import React, { useState, useEffect } from 'react';
import './Showcase.css'; // Ensure this matches your file structure

const VideoShowcase = () => {
    const [currentVideo, setCurrentVideo] = useState(null);
    const [isTextVisible, setIsTextVisible] = useState(false);
    
    const videos = {
        hello: '/videos/hello.mp4',
        thanks: '/videos/thanks.mp4',
        iloveyou: '/videos/love.mp4',
    };

    const handleVideoClick = (videoKey) => {
        setCurrentVideo(videos[videoKey]);
    };

    useEffect(() => {
        const timer = setTimeout(() => {
            setIsTextVisible(true);
        }, 1000); // Delay for fading in text

        return () => clearTimeout(timer);
    }, []);

    return (
        <div className="video-showcase">
            <div className="menu-icon" onClick={() => alert("Menu Clicked!")}>
                <div className="line"></div>
                <div className="line"></div>
                <div className="line"></div>
            </div>
            <div className="video-buttons">
                <button onClick={() => handleVideoClick('hello')}>Hello</button>
                <button onClick={() => handleVideoClick('thanks')}>Thanks</button>
                <button onClick={() => handleVideoClick('iloveyou')}>I Love You</button>
            </div>
            {currentVideo && (
                <div className="video-container">
                    <video 
                        src={currentVideo} 
                        controls 
                        autoPlay 
                        style={{ width: '100%', maxHeight: '400px' }} 
                    />
                </div>
            )}
            <div className="description">
                <div className={`description-text ${isTextVisible ? 'fade-in' : ''}`}>
                    Hey! This is a demo of an ASL Interpreter. Check out more information by clicking the hamburger menu!
                </div>
            </div>
        </div>
    );
};

export default VideoShowcase;
