import React, { useState } from 'react';
import './Showcase.css'; // Ensure this matches your file structure

const VideoShowcase = () => {
    const [currentVideo, setCurrentVideo] = useState(null);
    const [isHovered, setIsHovered] = useState(false); // State for hover effect

    // Define video sources
    const videos = {
        hello: '/videos/hello.mp4', // Replace with your video paths
        thanks: '/videos/thanks.mp4',
        iloveyou: '/videos/love.mp4',
    };

    // Function to handle video button click
    const handleVideoClick = (videoKey) => {
        setCurrentVideo(videos[videoKey]);
    };

    return (
        <div className="video-showcase">
            <div className="video-buttons">
                <button onClick={() => handleVideoClick('hello')}>Hello</button>
                <button onClick={() => handleVideoClick('thanks')}>Thanks</button>
                <button onClick={() => handleVideoClick('iloveyou')}>I Love You</button>
            </div>

            {currentVideo && (
                <div className="video-container">
                    <video 
                        src={currentVideo} 
                        controls 
                        autoPlay 
                        onMouseEnter={() => setIsHovered(true)} // Set hover state on mouse enter
                        onMouseLeave={() => setIsHovered(false)} // Reset hover state on mouse leave
                        style={{ width: '100%', maxHeight: '400px' }} 
                    />
                    {/* Description below the video */}
                    <div className={`description ${isHovered ? 'fade-in' : ''}`}>
                        This is a description of the project. Here you can talk about what this video represents and other relevant information.
                    </div>
                </div>
            )}
        </div>
    );
};

export default VideoShowcase;
