import React, { useState, useEffect, useRef } from 'react';
import './Showcase.css'; // Ensure you create this CSS file

const VideoShowcase = () => {
    const [currentVideo, setCurrentVideo] = useState(null); // State to hold the current video source
    const [showDescription, setShowDescription] = useState(false); // State to control the visibility of the description
    const descriptionRef = useRef(null); // Reference to the description element

    // Define video sources
    const videos = {
        hello: '/videos/hello.mp4', // Replace with your video paths
        thanks: '/videos/thanks.mp4',
        iloveyou: '/videos/love.mp4',
    };

    // Function to handle video button click
    const handleVideoClick = (videoKey) => {
        setCurrentVideo(videos[videoKey]);
    };

    // Function to handle scroll event
    const handleScroll = () => {
        const { top } = descriptionRef.current.getBoundingClientRect();
        if (top < window.innerHeight) { // When the element comes into the viewport
            setShowDescription(true);
            window.removeEventListener('scroll', handleScroll); // Remove listener after the first trigger
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <div className="video-showcase">
            <div className="video-buttons">
                <button onClick={() => handleVideoClick('hello')}>Hello</button>
                <button onClick={() => handleVideoClick('thanks')}>Thanks</button>
                <button onClick={() => handleVideoClick('iloveyou')}>I Love You</button>
            </div>

            {currentVideo && (
                <div className="video-container">
                    <video 
                        src={currentVideo} 
                        controls 
                        autoPlay 
                        style={{ width: '100%', maxHeight: '400px' }} 
                    />
                </div>
            )}

            {/* Scrollable Description Section */}
            <div className={`description ${showDescription ? 'visible' : ''}`} ref={descriptionRef}>
                <h2>About This Project</h2>
                <p>This project showcases a dynamic video presentation based on user input. The user can choose from different videos, and upon validation of the password, the selected video will play.</p>
                <p>Enjoy the visuals and explore the functionalities!</p>
            </div>
        </div>
    );
};

export default VideoShowcase;
