import React, { useState } from 'react';
import './PasswordChecker.css'; // Ensure this matches your file structure

const PasswordChecker = () => {
    const [password, setPassword] = useState('');
    const [showPassword, setShowPassword] = useState(false);
    const [isValid, setIsValid] = useState(null);
    const [errorMessage, setErrorMessage] = useState('');

    const validatePassword = () => {
        
        const isValid = hasUpperCase && hasLowerCase && hasNumbers && hasSpecialChars && password.length >= 8;

        setIsValid(isValid);
        setErrorMessage(isValid ? '' : 'Hint: Password is 3 characters long');
    };

    return (
        <div className="password-checker-container">
            {/* <h1 className="title">Password Checker</h1> Removed the title */}
            <form className="password-form" onSubmit={(e) => e.preventDefault() }>
                <div className="input-group">
                    <input
                        type={showPassword ? 'text' : 'password'}
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        className="password-input"
                        placeholder="Enter your password"
                    />
                </div>
                <div className="checkbox-group">
                    <input
                        type="checkbox"
                        className="checkbox"
                        checked={showPassword}
                        onChange={() => setShowPassword(!showPassword)}
                    />
                    <label className="checkbox-label">Show password</label>
                </div>
                <button type="submit" className="submit-button">Check Password</button>
                {errorMessage && <div className="error-message">{errorMessage}</div>}
                {isValid === true && <div className="success-message">Password is valid!</div>}
                {isValid === false && <div className="error-message">Password is invalid!</div>}
            </form>
        </div>
    );
};

export default PasswordChecker;
