import React, { useEffect, useRef, useState } from 'react';
import './CameraComponent.css';

const CameraComponent = () => {
    const videoRef = useRef(null);
    const [isSending, setIsSending] = useState(false);
    const [cap, setCap] = useState(null); // OpenCV VideoCapture object

    const getVideo = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ video: true });
            videoRef.current.srcObject = stream;

            // Initialize OpenCV VideoCapture
            const video = document.createElement('video');
            video.srcObject = stream;
            video.play();

            // Create a VideoCapture object
            const cv = window.cv;
            setCap(new cv.VideoCapture(video));
        } catch (error) {
            console.error("Error accessing webcam:", error);
        }
    };

    const sendFrame = async () => {
        if (cap) {
            const frame = new cv.Mat(videoRef.current.videoHeight, videoRef.current.videoWidth, cv.CV_8UC4);
            cap.read(frame); // Capture a frame

            // Convert the frame to a Base64 string
            const data = frame.toString(); // You might need to convert it correctly

            try {
                const response = await fetch('http://localhost:5000/video_feed', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ image: data }), // Send the image data
                });

                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }

                const result = await response.json();
                console.log("Frame processed:", result);
            } catch (error) {
                console.error('Error sending frame:', error);
            } finally {
                frame.delete(); // Clean up
            }
        }
    };

    useEffect(() => {
        getVideo();
        return () => {
            if (cap) {
                cap.delete(); // Clean up OpenCV VideoCapture
            }
        };
    }, []);

    const handleStart = () => {
        if (!isSending) {
            setIsSending(true);
            setInterval(sendFrame, 33); // ~30 FPS
        }
    };

    return (
        <div className="camera-container">
            <video ref={videoRef} autoPlay className="camera-feed" />
            <button 
                onClick={handleStart} 
                disabled={isSending} 
                style={{
                    backgroundColor: isSending ? 'darkgray' : 'blue',
                    color: 'white',
                    cursor: isSending ? 'not-allowed' : 'pointer',
                }}
            >
                {isSending ? 'Sending...' : 'Start Sending Frames'}
            </button>
        </div>
    );
};

export default CameraComponent;
